let imgExample = document.getElementById('showImg1')
xui.dropDown({
    id: 'style',
    getValue(){
        let str = `http://img.infinitynewtab.com/wallpaper/${xui.randomNum(1, 2000)}.jpg`;
        imgExample.src = str;
    }
});

xui.dropDown({
    id: 'count',
    getValue(){
        let str = `http://img.infinitynewtab.com/wallpaper/${xui.randomNum(1, 2000)}.jpg`;
        imgExample.src = str;
    }
});

xui.dropDown({
