imgExample.onclick = function(e){
    var style = document.getElementById('style').value;
    var count = document.getElementById('count').value;
    var delay = document.getElementById('delay').value;
    xui.showAnimatedImg({
        img: e.target,
        divide: count,
        delay: delay,
        animatedStyle: style,
